 SET @nUserNum2 = 0\r\n    \r\n    SELECT; @nUserNum1=UserInfo.UserNum    \r\n    FROM UserInfo\r\n    WHERE UserID = @userId\r\n    IF @nUserNum1 <> 0\r\n\t\tBEGIN    \r\n\t\t\t/*user exist*/    \r\n\t\t\tSET @nReturn = 1  \r\n\t\t\tSET NOCOUNT OFF      \r\n\t\t\tRETURN @nReturn \r\n\t\tEND\r\n    \r\n    SELECT; @nUserNum2=UserInfo.UserNum    \r\n    FROM UserInfo\r\n    WHERE UserEmail = @userMail\r\n    IF @nUserNum2 <> 0\r\n\t\tBEGIN    \r\n\t\t\t/*mail exist*/    \r\n\t\t\tSET @nReturn = 2 \r\n\t\t\tSET NOCOUNT OFF      \r\n\t\t\tRETURN @nReturn \r\n\t\tEND\r\n    \r\n    \r\n\tBEGIN TRAN\r\n\r\n\tINSERT; INTO UserInfo ( UserName, UserID, UserPass, UserPass2, UserSA, UserEmail, UserFullName, SGNum, SvrNum )\r\n\tVALUES (\t@userId, @userId, @userPass1, @userPass2, @userSA, @userMail, @userFullName, @SvrGrpNum, @SvrNum )\r\n\t\r\n\tSELECT; @error_var = @@ERROR, @rowcount_var = @@ROWCOUNT    \r\n\tIF @error_var <> 0 OR @rowcount_var = 0\r\n\t\tBEGIN\r\n\t\tROLLBACK TRAN\r\n\t\tSET @nReturn = -1\r\n\t\tEND\r\n\tELSE\r\n\t\tBEGIN\r\n\t\tCOMMIT TRAN\r\n\t\tEND\r\n\r\n    SET NOCOUNT OFF    \r\n    RETURN @nReturn \r\n\r\nGO;\r\n"],"metadata":{"urn":"Server[@Name='WIN-2BAJ6SC2P2P\\MSSQLSERVER01']/Database[@Name='RanUser']/StoredProcedure[@Name='user_register2' and @Schema='dbo']","object_type":"StoredProcedure"}},{"cell_type":"markdown","source":["# [dbo].[user_verify]"],"metadata":{"urn":"Server[@Name='WIN-2BAJ6SC2P2P\\MSSQLSERVER01']/Database[@Name='RanUser']/StoredProcedure[@Name='user_verify' and @Schema='dbo']","object_type":"StoredProcedure"}},{"outputs":[],"execution_count":0,"cell_type":"code","source":["/****** Object:  StoredProcedure [dbo].[user_verify]    Script Date: 2025/6/1 上午 01:19:04 ******/\r\nSET ANSI_NULLS ON\r\n","GO;\r\n","SET QUOTED_IDENTIFIER ON\r\n","GO;\r\n","\r\n\r\n\r\nCREATE PROCEDURE [dbo].[user_verify]\r\n    @userId        char(25),\r\n    @userPass     char(25),\r\n    @userIp        char(25),\r\n    @SvrGrpNum    int,\r\n    @SvrNum        int,   \r\n    @proPass    varchar(6), -- Ran dynamic password\r\n    @proNum     varchar(2), -- Ran dynamic password random number\r\n    @pcID_HWID  varchar(65), --PCID\r\n\t@pcID_MAC  varchar(65), --PCID\r\n\t@bPCIDNumLoginCheck   int,\t--PCID\r\n\t@bPCIDCheckLogin   int,\t--PCID\r\n\t@nPCIDNumLogin int,\r\n    @nReturn     int    OUTPUT\r\n\r\nAS        \r\n    DECLARE \r\n        @nAvailable    int,\r\n        @nUserNum    int,\r\n        @nState int,\r\n        -- Declare variables used in error checking.\r\n        @error_var int, \r\n        @rowcount_var int,\r\n        @nBlock int,\r\n        @BlockDate datetime,\r\n        @EndDate datetime,\r\n        @StrSql nvarchar(100),\r\n        @LastLoginDate datetime,\r\n        @UserLevel int,\r\n\r\n\t\t--login device check\r\n\t\t@UserLoginDeviceCheck int,\r\n\t\t@strLastHWID varchar(65),\r\n\t\t@strLastMAC varchar(65),\r\n\t\t@strLastIP varchar(25)\r\n\r\n \r\n    SET NOCOUNT ON\r\n\r\n    SET @nReturn = 0\r\n    SET @nUserNum = 0\r\n    \r\n    SELECT; @nUserNum=UserInfo.UserNum, \r\n           @nState=UserInfo.UserLoginState, \r\n           @nBlock=UserInfo.UserBlock, \r\n           @BlockDate=UserInfo.UserBlockDate,\r\n           @LastLoginDate=UserInfo.LastLoginDate,\r\n           @UserLevel = UserInfo.UserType,\r\n\r\n\t\t   --login device check\r\n\t\t   @UserLoginDeviceCheck = UserInfo.UserLoginDeviceCheck,\r\n\t\t   @strLastHWID = UserInfo.LastPCIDHWID,\r\n\t\t   @strLastMAC = UserInfo.LastPCIDMAC,\r\n\t\t   @strLastIP = UserInfo.LastIP\r\n\r\n    FROM UserInfo\r\n    WHERE UserID = @userId AND UserPass = @userPass AND UserAvailable = 1\r\n\r\n\r\n    -----------------------------------------------------------------\r\n-- ID / PWD check...\r\n    IF @nUserNum = 0\r\n    BEGIN        \r\n        SET @nReturn = 0        \r\n        RETURN @nReturn\r\n    END\r\n    ELSE\r\n    BEGIN        \r\n        SET @nReturn = 1        \r\n        IF @nState = 1\r\n        BEGIN\r\n\t\t\t-- login exist or user connected\r\n\t\t\t-- added logs\r\n\t\t\tINSERT; INTO LogExistLogin (UserNum, UserID, LogIpAddress) \r\n\t\t\tVALUES (@nUserNum, @userId, @userIp)    \r\n\t\t\t\r\n            SET @nReturn = 5\r\n            RETURN @nReturn \r\n--\tupdate userinfo set userloginstate =0\r\n--\twhere usernum =@nUserNum \r\n       END\r\n    END\r\n\r\n\t---------\r\n\t---Login delay check\r\n\t---works only on non user gm\r\n\tIF @UserLevel < 20\r\n\tBEGIN\r\n\t\tIF (SELECT; DATEDIFF(SECOND, @LastLoginDate, GETDATE()) ) < 2\r\n\t\tBEGIN\r\n\t\t\tSET @nReturn = 30\r\n\t\t\tRETURN @nReturn\r\n\t\tEND\r\n\tEND \r\n\t\r\n\t---PCID\r\n\tIF @bPCIDNumLoginCheck = 1\r\n\tBEGIN\r\n\t\tIF (SELECT; COUNT(*) FROM UserKey WITH (NOLOCK) WHERE PCIDHWID = @pcID_HWID AND PCIDMAC = @pcID_MAC) = @nPCIDNumLogin\r\n\t\tBEGIN\r\n\t\t\t-- pc active\r\n\t\t\tSET @nReturn = 31\r\n\t\t\tRETURN @nReturn\r\n\t\tEND\r\n\tEND\r\n\t---END PCID\r\n\t\r\n\t---PCID Check\r\n\t---block type \r\n\t---1 = hwid only\r\n\t---2 = mac only\r\n\t---3 = both hwid and mac\r\n\tIF @bPCIDCheckLogin = 1\r\n\tBEGIN\r\n\t\tIF (SELECT; COUNT(*) FROM BlockPCID WITH (NOLOCK) WHERE BlockHWID = @pcID_HWID AND BlockTYPE = 1) > 0\r\n\t\tBEGIN\r\n\t\t\t-- pcid banned\r\n\t\t\tSET @nReturn = 32\r\n\t\t\tRETURN @nReturn\r\n\t\tEND\r\n\r\n\t\tIF (SELECT; COUNT(*) FROM BlockPCID WITH (NOLOCK) WHERE BlockMAC = @pcID_MAC AND BlockTYPE = 2) > 0\r\n\t\tBEGIN\r\n\t\t\t-- pcid banned\r\n\t\t\tSET @nReturn = 32\r\n\t\t\tRETURN @nReturn\r\n\t\tEND\r\n\r\n\t\tIF (SELECT; COUNT(*) FROM BlockPCID WITH (NOLOCK) WHERE BlockHWID = @pcID_HWID AND  BlockMAC = @pcID_MAC AND BlockTYPE = 3 ) > 0\r\n\t\tBEGIN\r\n\t\t\t-- pcid banned\r\n\t\t\tSET @nReturn = 32\r\n\t\t\tRETURN @nReturn\r\n\t\tEND\r\n\r\n\tEND\r\n\t---END PCID Check\r\n\t\r\n\tIF (SELECT; COUNT(*) FROM BlockAddress WITH (NOLOCK) WHERE BlockAddress = @userIp) > 0\r\n    BEGIN\r\n        declare @params as nvarchar(100)        \r\n        SET @StrSQL = 'SELECT; @ordercnt=count(userid) FROM Randpas